import React, { useEffect, useState } from 'react';
import { QueryClient, QueryClientProvider, useQuery } from 'react-query'
import axios from 'axios';
import AWS from 'aws-sdk';

const queryClient = new QueryClient()

AWS.config.update({
	region: 'us-east-2',
	accessKeyId: 'AKIA4KI6HSV4O4EDEZP5',
	secretAccessKey: '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'
});
const dynamoDB = new AWS.DynamoDB.DocumentClient();

export default function App() {
	return (
		<QueryClientProvider client={queryClient}>
			<Example />
		</QueryClientProvider>
	)
}

function ReadDynamoDB() {
		const [fetchPosts, setFetchDDBPosts] = useState(false);

		const [tableData, setTableData] = useState([]);
	useEffect(() => {
		const fetchData = async () => {
			try {
				const params = {
					TableName: 'TextWeb'
				};
				const response = await dynamoDB.scan(params).promise();
				setTableData(response.Items);
				console.error('REad  DynamoDB table:', response.Items);
			} catch (error) {
				console.error('Error reading DynamoDB table:', error);
			}
		}
	});

		if (isLoading && fetchPosts) return "Loading...";

	if (error) return "An error has occurred: " + error.message;

	return (
		<div>
			<button onClick={() => setFetchDDBPosts(true)}>
				Fetch posts using useQuery{" "}
			</button>
			<h1>Posts</h1>
			{data?.map((post) => {
				return (
					<div style={{ display: "flex" }}>
						<span>{post.id}-&nbsp;</span>
						<div>{post.title}</div>
					</div>
				);
			})}
		</div>
	);
}


function Example() {
	const [fetchPosts, setFetchPosts] = useState(false);
	const { isLoading, error, data } = useQuery(
		["posts"],
		() =>
			axios
				.get("https://jsonplaceholder.typicode.com/posts")
				.then((res) => res.data),
		{
			enabled: fetchPosts
		}
	);

	if (isLoading && fetchPosts) return "Loading...";

	if (error) return "An error has occurred: " + error.message;

	return (
		<div>
			<button onClick={() => setFetchPosts(true)}>
				Fetch posts using useQuery{" "}
			</button>
			<h1>Posts</h1>
			{data?.map((post) => {
				return (
					<div style={{ display: "flex" }}>
						<span>{post.id}-&nbsp;</span>
						<div>{post.title}</div>
					</div>
				);
			})}
		</div>
	);
}
