import React, { useState } from 'react';
import AWS from 'aws-sdk';

AWS.config.update({
	region: 'us-east-2',
	accessKeyId: 'AKIA4KI6HSV4O4EDEZP5',
	secretAccessKey: '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'
});

const dynamoDB = new AWS.DynamoDB();
const ReadDataButton = () => {
	const [data, setData] = useState('');

	const handleButtonClick = () => {
		const params = {
			TableName: 'your-dynamodb-table',
			Key: {
				primaryKey: { S: 'your-primary-key-value' }
			}
		};

		dynamoDB.getItem(params, (err, result) => {
			if (err) {
				console.error(err);
			} else {
				const item = result.Item;
				setData(item ? item.attributeName.S : 'No data found');
			}
		});
	};

	return (
		<div>
			<button onClick={handleButtonClick}>Read Data</button>
			<p>{data}</p>
		</div>
	);
};

export default ReadDataButton;
