//import logo from './logo.svg';
import './App.css';

import React, { useEffect, useState } from 'react';
import AWS from 'aws-sdk';
import { useQuery } from "react-query";

import './ReadDynamoDBTable.css'; // Import the CSS file for styling

AWS.config.update({
	region: 'us-east-2',
	accessKeyId: 'AKIA4KI6HSV4O4EDEZP5',
	secretAccessKey: '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'
});

const dynamodb = new AWS.DynamoDB();
const tableName = 'TextWeb';

const params = {
	TableName: tableName,
};

function App() {
	const [table, setTable] = useState(null);
	const [data, setData] = useState([]);

	const { isLoading, error, data: queryData } = useQuery("table", () => {
		const client = dynamodb;
		return client.query({
			TableName: tableName,
		});
	});

	useEffect(() => {
		if (isLoading) {
			return;
		}

		if (error) {
			console.log(error);
			return;
		}

		setData(queryData.Items);
	}, [isLoading, error]);

	return (
		<div>
			<h1>Table</h1>
			{data.map((item) => (
				<div key={item.id}>{item.name}</div>
			))}
		</div>
	);
};

export default App;
