import React, { useState } from 'react';
import AWS from 'aws-sdk';

AWS.config.update({
	region: 'us-east-2',
	accessKeyId: 'AKIA4KI6HSV4O4EDEZP5',
	secretAccessKey: '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'
});

const dynamoDB = new AWS.DynamoDB();

const ReadDataButton = () => {
	const [data, setData] = useState([]);

	const handleButtonClick = () => {
		const params = {
			TableName: 'TextWeb',
		};

		dynamoDB.scan(params, (err, result) => {
			if (err) {
				console.error(err);
			} else {
				const items = result.Items;
				console.log("items " +items)
				console.log("length " + items.map.length)

				setData(items.map);
			}
		});
	};

	return (
		<div>
			<button onClick={handleButtonClick}>Read Data</button>
			{data.map((item, index) => (
        		<p key={index}>{item}</p>
      		))}
		</div>
	);
};

export default ReadDataButton;
