//import logo from './logo.svg';
import './App.css';

import React, { useEffect, useState } from 'react';
import AWS from 'aws-sdk';
import './ReadDynamoDBTable.css'; // Import the CSS file for styling

AWS.config.update({
  region: 'us-east-2',
  accessKeyId: 'AKIA4KI6HSV4O4EDEZP5',
  secretAccessKey: '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'
});

const dynamodb = new AWS.DynamoDB();

function App() {
  const [tableData, setTableData] = useState([]);

useEffect(() => {
  dynamodb.scan({ TableName: 'TextWeb' }, (err, data) => {
    if (err) {
      console.error('Error retrieving data:', err);
    } else {
      setTableData(data.Items);
    }
  });
}, []);


  return (
    <table>
    <thead>
      <tr>
        <th>Column 1</th>
        <th>Column 2</th>
        <th>Column 3</th>
        <th>Column 4</th>
      </tr>
    </thead>
    <tbody>
      {tableData.map((item, index) => (
        <tr key={index}>
          <td>{item.Column1.S}</td>
          <td>{item.Column2.S}</td>
          <td>{item.Column3.S}</td>
          <td>{item.Column4.S}</td>
        </tr>
      ))}
    </tbody>
  </table>
  );
};

export default App;
